INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-11-30 07:52:22', '2013-11-30 07:52:22', 'Добро пожаловать в WordPress. Это ваша первая запись. Отредактируйте или удалите её, затем пишите!', 'Привет, мир!', '', 'trash', 'open', 'open', '', '%d0%bf%d1%80%d0%b8%d0%b2%d0%b5%d1%82-%d0%bc%d0%b8%d1%80', '', '', '2013-11-30 17:40:50', '2013-11-30 13:40:50', '', 0, 'http://eremont.dev/?p=1', 0, 'post', '', 1),
(2, 1, '2013-11-30 07:52:22', '2013-11-30 07:52:22', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n				<tbody><tr>\r\n					<td valign="top">\r\n					\r\n						<noindex>\r\n						<table width="100%" cellpadding="0" cellspacing="0" border="0" id="vn_text_main_container">\r\n							<tbody><tr>\r\n								<td width="100%" style="padding: 10px 30px 0 0;"><div style="min-height:5px;">\r\n<!--\r\n<span style="color: rgb(255, 102, 0);" align="justify"> <font size="5">До 22 ноября <b>скидка 25%</b> на дизайн-проект<br><br> и 10% на ремонт!</font></span>\r\n//--> \r\n					<table width="95%" border="0" cellspacing="0" cellpadding="0">\r\n						<tbody><tr>\r\n							<td width="147"><img src="/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_01_1.gif" alt="" width="147" height="49" border="0"></td>\r\n							<td style="background:url(/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_02_1.gif)"><img src="/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_02_1.gif" width="1" height="49" border="0"></td>\r\n							<td width="83"><img src="/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_03_1.gif" alt="" width="83" height="49" border="0"></td>\r\n						</tr>\r\n					</tbody></table>\r\n					<table width="95%" border="0" cellspacing="0" cellpadding="0" style="background:#ffffff url(/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_04_1.gif) repeat-y left;">\r\n						<tbody><tr>\r\n							<td valign="top" style="background:url(/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_08.gif) repeat-x bottom;">\r\n							<div style="min-height:74px; background:url(/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_07.gif) no-repeat left bottom;" class="popravka_dlya_ie">\r\n								<div style="position:relative; left: 44px;">Нам 17 лет! И мы Вам <font color="#f16522">дарим </font><b>дизайн-проект</b>! Полный возврат стоимости дизайн-проекта на конечной стадии ремонта. \r\n<!--Кроме того, заключившим договор до 19 августа на комплекс услуг - монтаж  сигнализации <font color="#f16522"><b>бесплатно</b>\r\n ! -->\r\n 																<div style="padding:5px 0 15px 0;"><a href="/dizain-proekt/besplatno/">подробнее</a> <a href="/dizain-proekt/besplatno/"><img src="/bitrix/templates/eremont_main_2007/images_tpl/acciya_but_01.gif" alt="" width="15" height="14" border="0" align="absmiddle"></a></div>\r\n																</div>\r\n							</div>\r\n							</td>\r\n							<td width="83" valign="bottom" style="background:url(/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_06_1.gif); background-position:right;background-repeat:repeat-y;"><img src="/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_09_1.gif" alt="" width="83" height="74" border="0"></td>\r\n						</tr>\r\n					</tbody></table>\r\n					<div><img src="/bitrix/templates/eremont_main_2007/images_tpl/vn_ac_10.gif" alt="" width="209" height="16" border="0"></div>\r\n					</div>\r\n\r\n\r\n\r\n\r\n</td>\r\n							</tr>\r\n						</tbody></table>\r\n						</noindex>\r\n					\r\n						<table width="100%" border="0" cellspacing="0" cellpadding="0" id="vn_text_main_container">\r\n							<tbody><tr>\r\n								<td width="11%"><img src="/bitrix/templates/eremont_main_2007/images_tpl/pixel.gif" width="40" height="1" border="0" alt=""></td>\r\n								<td style="padding: 5px 0 10px 0;">\r\n\r\n																		<p class="chain"><a href="/">Главная страница</a>&nbsp;&gt;&nbsp;<a href="/design/">Дизайн интерьеров</a></p>									\r\n									<h1>Цены на проектные работы</h1>\r\n									\r\n									 \r\n<table class="table-1"> 	 \r\n  <tbody> 		 \r\n    <tr> <th nowrap="nowrap">№</th> <th nowrap="nowrap" colspan="2">Наименование проекта</th> <th nowrap="nowrap">Цена</th> <th> \r\n        <br>\r\n       </th> 		</tr>\r\n   \r\n    <tr><td>1.</td> 	<td colspan="2"><span style="color: rgb(213, 157, 33);">Эскизный проект</span> \r\n        <br>\r\n       &nbsp; <small><font color="#FF0000">!</font> В подарок как приложение к ремонту \r\n          <br>\r\n         </small></td><td align="center">&nbsp; 750 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#1">подробнее </a>	</td></tr>\r\n   \r\n    <tr><td>2.</td> 	<td colspan="2"><span style="color: rgb(213, 157, 33);">Рабочий проект</span> \r\n        <br>\r\n       &nbsp; <small><font color="#FF0000">!</font> В подарок как приложение к ремонту</small></td><td align="center">от 800 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#2">подробнее </a>	</td></tr>\r\n   \r\n    <tr><td>3.</td> 	<td colspan="2"><span style="color: rgb(213, 157, 33);">Инженерный проект</span></td><td></td><td><a href="http://www.eremont.ru/design/price.html#3">подробнее </a>	</td></tr>\r\n   \r\n    <tr><td></td><td>о</td> 	<td>Отопление</td><td align="center">180 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#11">подробнее </a>	</td></tr>\r\n   \r\n    <tr><td></td><td>вик</td><td>Кондиционирование и вентиляция</td><td align="center">230 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#12">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>вк</td><td>Водоснабжение и канализация</td><td align="center">180 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#13">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>эо</td><td>Электрооборудование</td><td align="center">180 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#14">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>сс</td><td>Интернет, телефон, телевидение, домофон</td><td align="center">180 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#15">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>дк</td><td>Домашний кинотеатр</td><td align="center">13 000 руб</td><td><a href="http://www.eremont.ru/design/price.html#16">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>опс</td><td>Охранно-пожарная сигнализация</td><td align="center">180 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#17">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>от</td><td>Охранное телевидение</td><td align="center">180 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#18">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>см</td><td>Мультирум</td><td align="center">150 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#19">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>вп</td><td>Встроенный пылесос</td><td align="center">130 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#20">подробнее </a></td></tr>\r\n   \r\n    <tr><td></td><td>уо</td><td>Управление освещением</td><td align="center">от 220 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#21">подробнее </a></td></tr>\r\n   \r\n    <tr><td>4.</td> 	<td colspan="2">Визуализация</td><td align="center">от 500 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#4">подробнее </a>	</td></tr>\r\n   \r\n    <tr><td>5.</td> 	<td colspan="2">Декорирование</td><td></td><td><a href="http://www.eremont.ru/design/price.html#5">подробнее </a>	</td></tr>\r\n   \r\n    <tr><td>6.</td><td colspan="2">Авторский надзор</td><td align="center">от 700 руб. м<sup>2</sup></td><td><a href="http://www.eremont.ru/design/price.html#6">подробнее </a>	</td></tr>\r\n   \r\n    <tr><td>7.</td><td colspan="2">Исполнительная документация</td><td align="center">&nbsp; \r\n        <br>\r\n       </td><td><a href="http://www.eremont.ru/design/price.html#7">подробнее </a>	</td></tr>\r\n   </tbody>\r\n </table>\r\n \r\n<br>\r\n \r\n<br>\r\n \r\n<br>\r\n \r\n<a name="1"></a>\r\n \r\n<h2>1. Эскизный проект.</h2>\r\n \r\n<p align="left">Стоимость эскизного проекта <font color="#9d0039"><b>750 рублей</b></font> за квадратный метр. \r\n  <br>\r\n \r\n</p><p></p>\r\n \r\n<ol type="1"> \r\n  <li>Обмерный чертеж с привязкой инженерных коммуникаций. </li>\r\n \r\n  <li>Варианты планировочных решений. </li>\r\n \r\n  <li>План расстановки мебели (схематично, без прорисовки элементов мебели). </li>\r\n \r\n  <li>План пола с указанием типа напольного покрытия. </li>\r\n \r\n  <li>План потолка (без указания размеров и разрезов). </li>\r\n \r\n  <li>План размещения сантехники без привязки выпусков. </li>\r\n \r\n  <li>Перспективы помещений, выполненные в программе ArchiCad. </li>\r\n \r\n  <li>Консультации по выбору мебели, сантехники, напольных, настенных и потолочных покрытий в каждое помещение. Поездки по салонам и магазинам. Общее количество выездов в течении выполнения проекта не более 5 поездок, если выполняется только эскизный проект и не более 8 поездок, если выполняется эскизный и рабочий проект. Данные поездки идут как бесплатное приложение к договору на проектирование. Если эти поездки не используются в период проектирования, то по его окончанию поездки оплачиваются, 4500 рублей каждая. </li>\r\n </ol>\r\n \r\n<p>Разработка проектной документации выполняется в архитектурной программе ArchiCad 11. \r\n  <br>\r\n </p>\r\n \r\n<br>\r\n \r\n<a name="2"></a>\r\n \r\n<h2>2. Рабочий проект</h2>\r\n \r\n<p align="left">Стоимость выполнения рабочего проекта <b><font color="#9d0039">800-1350 рублей</font></b> за квадратный метр, в зависимости от площади объекта. \r\n  <br>\r\n </p>\r\n \r\n<ol type="1"> \r\n  <li>Обмерный чертеж с привязкой инженерных коммуникаций. </li>\r\n \r\n  <li>План демонтажа перегородок и инженерных коммуникаций. </li>\r\n \r\n  <li>План возводимых перегородок с маркировкой оконных и дверных проемов. </li>\r\n \r\n  <li>План помещения после перепланировки с размерами. </li>\r\n \r\n  <li>План размещения санитарно-технического оборудования с привязкой выпусков и приложением монтажных чертежей от производителя. </li>\r\n \r\n  <li>План потолка с указанием типа используемого материала, отдельных узлов и сечений. (Количество чертежей зависит от уровня сложности потолка). </li>\r\n \r\n  <li>План размещения осветительных приборов, привязка выпусков освещения, план выключателей с указанием включения групп светильников. </li>\r\n \r\n  <li>Спецификация осветительного оборудования с указанием их типа и мощности и др. специальных требований. </li>\r\n \r\n  <li>План размещения электрических розеток и электровыводов с привязкой геометрических размеров. </li>\r\n \r\n  <li>Спецификация электромонтажной арматуры с указанием типа изделия и др. специальных требований. </li>\r\n \r\n  <li>План размещения электрического подогрева пола с привязкой регулятора. </li>\r\n \r\n  <li>План пола с указанием: отметки уровня пола, типа напольного покрытия, рисунка и размеров. Разрез конструкции пола с указанием слоев покрытия. (Количество чертежей зависит от уровня сложности). </li>\r\n \r\n  <li>Экспликация напольных покрытий с указанием площади и артикула выбранного материала. </li>\r\n \r\n  <li>Разрез и развертка стен с декоративными элементами. (Количество чертежей зависит от количества декоративных элементов). </li>\r\n \r\n  <li>Развертка стен с раскладкой плитки с указание размеров, артикула и площади выбранного материала. </li>\r\n \r\n  <li>Чертежи заказных изделий (выполняется в случае необходимости). </li>\r\n \r\n  <li>План расстановки выбранной мебели. </li>\r\n \r\n  <li>Экспликация штучных элементов с указание артикулов, количества и места размещения. </li>\r\n \r\n  <li>Спецификация дверей с указанием размеров дверных проемов. </li>\r\n \r\n  <li>Ведомость отделки. </li>\r\n </ol>\r\n \r\n<p>Разработка проектной документации выполняется в архитектурной программе ArchiCad 11. \r\n  <br>\r\n </p>\r\n \r\n<p> \r\n  <br>\r\n </p>\r\n \r\n<a name="3"></a>\r\n \r\n<h2>3. Проект инженерных коммуникаций</h2>\r\n \r\n<a name="11"></a>\r\n \r\n<ol type="1"> \r\n  <li><strong>Проект по <font color="#800080">отоплению</font> (О):</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Общие данные (отопление);</li>\r\n \r\n  <li>Принципиальная схема подключения отопительных приборов;</li>\r\n \r\n  <li>Теплотехнический расчет;</li>\r\n \r\n  <li>Спецификация на оборудование;</li>\r\n \r\n  <li>Схема подключения конструкции водяных полов (выполняется в случае необходимости)</li>\r\n </ul>\r\n \r\n<p>Стоимость проекта по отоплению 18000 руб до 100 кв. м, свыше 180 руб за кв. метр общей площади пола.</p>\r\n \r\n<p>Стоимость проекта по отоплению с теплотехническим расчетом 23 000 руб до 100кв.м, свыше 230 руб за кв. м. общей площади пола. \r\n  <br>\r\n </p>\r\n \r\n<p> </p>\r\n \r\n<a name="12"></a>\r\n \r\n<ol type="1" start="2"> \r\n  <li> \r\n<a name="cond"></a>\r\n <strong>Проект по <font color="#800080">кондиционированию и вентиляции</font> (В и К):</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Общие данные (конд.);</li>\r\n \r\n  <li>Принципиальная схема подключения;</li>\r\n \r\n  <li>Спецификация на оборудование;</li>\r\n \r\n  <li>Общие данные (вентиляция);</li>\r\n \r\n  <li>Принципиальная схема;</li>\r\n \r\n  <li>Расчет по вентиляции; прокладки воздуховодов</li>\r\n \r\n  <li>Спецификация на оборудование.</li>\r\n </ul>\r\n \r\n<p>Стоимость проекта по вентиляции и кондиционирования 23000 руб до 100 кв. м., свыше <font color="#800080">230 руб за кв. метр</font> общей площади пола. Стоимость проекта кондиционирования 18000 руб до 100 кв.м, свыше 180 руб за кв. метр общей площади пола.</p>\r\n \r\n<p>Получение исходных данных для проектирования в случае получения Подрядчиком стоимость 3 600руб.</p>\r\n \r\n<p> </p>\r\n \r\n<a name="13"></a>\r\n \r\n<ol type="1" start="3"> \r\n  <li> \r\n<a name="water"></a>\r\n <strong>Проект по <font color="#800080">водоcнабжению и канализации</font> (ВК).</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Общие данные;</li>\r\n \r\n  <li>Принципиальная схема;</li>\r\n \r\n  <li>Схема разводки труб канализации и водопровода в аксонометрии;</li>\r\n \r\n  <li>Спецификация.</li>\r\n </ul>\r\n \r\n<p>Стоимость проекта по водопроводу и канализации 18000 руб до 100 кв. м, свыше <font color="#800080">180 руб за кв. метр</font> общей площади пола. </p>\r\n \r\n<p> </p>\r\n \r\n<a name="14"></a>\r\n \r\n<ol type="1" start="4"> \r\n  <li> \r\n<a name="electro"></a>\r\n <strong>Проект по <font color="#800080">электрооборудованию</font> (ЭО).</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта;</li>\r\n \r\n  <li>Пояснения к проекту;</li>\r\n \r\n  <li>Расчетные однолинейные схемы групповых щитов;</li>\r\n \r\n  <li>Планы силовых распределительных электросетей;</li>\r\n \r\n  <li>Планы групповых осветительных электросетей;</li>\r\n \r\n  <li>Система уравнивания потенциалов; </li>\r\n \r\n  <li>Ведомость ссылочных документов;</li>\r\n\r\n  <li>План расположения оборудования протечки воды;</li>\r\n \r\n  <li>Спецификация материалов и оборудования по желанию клиента;</li>\r\n </ul>\r\n \r\n<p>Стоимость проекта по электрооборудованию 18000 руб до 100 кв. м, свыше <font color="#800080">180 руб за кв. метр</font> общей площади пола. \r\n  <br>\r\n </p>\r\n \r\n<p>Получение исходных данных для проектирования в случае получения Подрядчиком стоимость 3 600руб. \r\n  <br>\r\n </p>\r\n \r\n<p> </p>\r\n \r\n<a name="15"></a>\r\n \r\n<ol type="1" start="5"> \r\n  <li><strong>Проект по <font color="#800080">слаботочным системам</font> (СС) /<font color="#800080">Интернет, телефон, телевидение, домофон</font>/.</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта;</li>\r\n \r\n  <li>Список ссылочных документов;</li>\r\n \r\n  <li>Пояснения к проекту;</li>\r\n \r\n  <li>Структурная схема;</li>\r\n \r\n  <li>План слаботочных линий телевидения (с привязкой геометрических размеров розеток);</li>\r\n \r\n  <li>План слаботочных линий телефона, Интернета (с привязкой геометрических размеров розеток);</li>\r\n \r\n  <li>План расположения домофона (с привязкой геометрических размеров);</li>\r\n \r\n  <li>Схема слаботочного щита;</li>\r\n \r\n  <li>Спецификация;</li>\r\n \r\n  <li>Смета на монтаж и оборудование.</li>\r\n </ul>\r\n \r\n<p>Стоимость проекта слаботочных сетей 18000 рублей до 100 кв.м , свыше - <font color="#800080">180 р. за кв. м.</font> общей площади пола.</p>\r\n Получение исходных данных для проектирования в случае получения Подрядчиком стоимость 5 800руб. \r\n<br>\r\n \r\n<p> </p>\r\n \r\n<a name="16"></a>\r\n \r\n<ol type="1" start="6"> \r\n  <li><strong>Проект по <font color="#800080">домашнему кинотеатру</font> (ДК).</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта; </li>\r\n \r\n  <li>Список ссылочных документов; </li>\r\n \r\n  <li>Пояснения к проекту; </li>\r\n \r\n  <li>План расположения оборудования (подбор оборудования: DVD, телевизор, AV ресивер, сабвуфер, 5 акустических систем, мебельная стойка); </li>\r\n \r\n  <li>План прокладки акустического кабеля (с привязкой геометрических размеров акустических систем); </li>\r\n \r\n  <li>План прокладки кабеля в стойке (с привязкой геометрических размеров выводов кабеля); </li>\r\n \r\n  <li>План расположения розеток (с привязкой геометрических размеров розеток); </li>\r\n \r\n  <li>Схема подключения оборудования; </li>\r\n \r\n  <li>Спецификация; </li>\r\n \r\n  <li>Смета на монтаж и оборудование. </li>\r\n </ul>\r\n \r\n<p>Стоимость проекта домашнего кинотеатра 13 000 рублей, подбор дополнительного телевизора 4000 рублей, подбор дополнительного комплекта домашнего кинотеатра (состав оборудования: DVD, телевизор, AV ресивер, сабвуфер, 5 акустических систем, мебельная стойка) 7000 рублей.</p>\r\n \r\n<p>Получение исходных данных для проектирования: предоставляет Заказчик.</p>\r\n \r\n<p> </p>\r\n \r\n<a name="17"></a>\r\n \r\n<ol type="1" start="7"> \r\n  <li><strong>Проект по <font color="#800080">охранно-пожарной сигнализации</font> (ОПС).</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта; </li>\r\n \r\n  <li>Список ссылочных документов; </li>\r\n \r\n  <li>Пояснения к проекту; </li>\r\n \r\n  <li>Структурная схема; </li>\r\n \r\n  <li>План расстановки охранных извещателей (с привязкой геометрических размеров извещателей); </li>\r\n \r\n  <li>План прокладки шлейфов охранной сигнализации (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>План расстановки пожарный извещателей (с привязкой геометрических размеров извещателей); </li>\r\n \r\n  <li>План прокладки шлейфов пожарной сигнализации (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>Спецификация; </li>\r\n \r\n  <li>Смета на монтаж и оборудование. </li>\r\n </ul>\r\n \r\n<p>Стоимость проекта охранно-пожарной сигнализации <font color="#800080">180 руб/кв. м</font> общей площади пола, но не менее 18 000 рублей.</p>\r\n \r\n<p>Согласование пожарной сигнализации 3000 рублей.</p>\r\n \r\n<p>Получение исходных данных для проектирования: предоставляет Заказчик. </p>\r\n \r\n<p> </p>\r\n \r\n<a name="18"></a>\r\n \r\n<ol type="1" start="8"> \r\n  <li><strong>Проект по охранному телевидению (стадия ОТ).</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта; </li>\r\n \r\n  <li>Список ссылочных документов; </li>\r\n \r\n  <li>Пояснения к проекту; </li>\r\n \r\n  <li>Структурная схема; </li>\r\n \r\n  <li>План расстановки видеокамер (с привязкой геометрических размеров видеокамер); </li>\r\n \r\n  <li>План прокладки кабеля питания (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>План прокладки видеокабеля (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>Угол обзора видеокамеры; </li>\r\n \r\n  <li>Спецификация; </li>\r\n \r\n  <li>Смета на монтаж и оборудование. </li>\r\n </ul>\r\n \r\n<p>Стоимость проекта охранного телевидения 180 р. за кв. м. общей площади пола, но не менее 18 000 руб.</p>\r\n \r\n<p>Получение исходных данных для проектирования: предоставляет Заказчик.</p>\r\n \r\n<p> </p>\r\n \r\n<a name="19"></a>\r\n \r\n<ol type="1" start="9"> \r\n  <li><strong>Проект по системе мультирум (стадия СМ). *Опция*</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта; </li>\r\n \r\n  <li>Список ссылочных документов; </li>\r\n \r\n  <li>Пояснения к проекту; </li>\r\n \r\n  <li>Структурная схема; </li>\r\n \r\n  <li>План расстановки терминалов управления (с привязкой геометрических размеров терминалов); </li>\r\n \r\n  <li>План прокладки слаботочного кабеля (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>План расстановки акустических систем (с привязкой геометрических размеров колонок); </li>\r\n \r\n  <li>План прокладки акустического кабеля (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>Рекомендуемая стойка под оборудование; </li>\r\n \r\n  <li>Спецификация; </li>\r\n \r\n  <li>Смета на монтаж и оборудование. </li>\r\n </ul>\r\n \r\n<p>Стоимость проекта системы мультирум 150 р. за кв. м общей площади пола, не менее 18 000 руб.</p>\r\n \r\n<p>Получение исходных данных для проектирования: предоставляет Заказчик.</p>\r\n \r\n<p> </p>\r\n \r\n<a name="20"></a>\r\n \r\n<ol type="1" start="10"> \r\n  <li><strong>Проект по встроенному пылесосу (стадия ВП). * Опция *</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта; </li>\r\n \r\n  <li>Список ссылочных документов; </li>\r\n \r\n  <li>Пояснения к проекту; </li>\r\n \r\n  <li>План прокладки воздуховодов (с привязками геометрических размеров пневмосовков и пневморозеток); </li>\r\n \r\n  <li>Монтажная схема трубопровода; </li>\r\n \r\n  <li>Монтажная схема розеток и силового блока; </li>\r\n \r\n  <li>План напольного покрытия; </li>\r\n \r\n  <li>Спецификация; </li>\r\n \r\n  <li>Смета на монтаж и оборудование. </li>\r\n </ul>\r\n \r\n<p>Стоимость проекта встроенный пылесос до 130 кв.м 15 000 рублей , свыше - 150 р. за кв. м. общей площади пола </p>\r\n \r\n<p>Получение исходных данных для проектирования: предоставляет Заказчик.</p>\r\n \r\n<p> </p>\r\n \r\n<a name="21"></a>\r\n \r\n<ol type="1" start="11"> \r\n  <li><strong>Проект по управление освещением (стадия УО). *Опция*</strong> </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Состав проекта; </li>\r\n \r\n  <li>Список ссылочных документов; </li>\r\n \r\n  <li>Пояснения к проекту; </li>\r\n \r\n  <li>Структурная схема; </li>\r\n \r\n  <li>План расстановки светильников (с привязкой геометрических размеров светильников, мощности потребления и группы); </li>\r\n \r\n  <li>План прокладки силового кабеля (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>План расстановки выключателей (с привязкой геометрических размеров выключателей); </li>\r\n \r\n  <li>План прокладки кабеля питания выключателей (с привязкой геометрических размеров кабель выводов); </li>\r\n \r\n  <li>План расположения оборудования в щите; </li>\r\n \r\n  <li>Сценарии; </li>\r\n \r\n  <li>Спецификация; </li>\r\n \r\n  <li>Смета на монтаж и оборудование. </li>\r\n </ul>\r\n \r\n<p>Стоимость проекта управления освещением до 300 кв.м 250р. за м. кв. , свыше 220 р. за м. кв. общей площади пола.</p>\r\n \r\n<p>Получение исходных данных для проектирования: предоставляет Заказчик.</p>\r\n \r\n<p> </p>\r\n \r\n<a name="4"></a>\r\n \r\n<h2>4. Визуализация проекта. </h2>\r\n \r\n<ol type="1"> \r\n  <li>Визуализация проекта производится в программе 3D Studio Max при выбранных материалах и мебели. </li>\r\n \r\n  <li>Стоимость визуализации: </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>500 рублей за 1 кв.м.</li>\r\n \r\n  <li>600 рублей за 1 кв.м., при заказе от 80 до 100 кв. м.</li>\r\n \r\n  <li>800 рублей за 1 кв.м., при заказе менее чем 80 кв. м. \r\n    <br>\r\n   </li>\r\n \r\n  <li>Минимальная стоимость заказа 10000 рублей.</li>\r\n </ul>\r\n \r\n<ol type="1" start="3"> \r\n  <li>Визуализация выдается записанная на компакт диске. </li>\r\n </ol>\r\n \r\n<p> </p>\r\n \r\n<a name="5"></a>\r\n \r\n<h2>5. Декорирование. </h2>\r\n \r\n<ol type="1"> \r\n  <li>Подбор декоративных элементов интерьера выполняется после выполнения ремонтно-отделочных работ и включает в себя: </li>\r\n </ol>\r\n \r\n<ul> \r\n  <li>Подбор картин, рисунков, настенной графики.</li>\r\n \r\n  <li>Подбор скульптур и др.</li>\r\n \r\n  <li>Подбор столовых приборов, сервизов и скатертей.</li>\r\n \r\n  <li>Подбор штор и гардин.</li>\r\n \r\n  <li>Подбор постельного белья.</li>\r\n \r\n  <li>Подбор аксессуаров в различные помещения.</li>\r\n \r\n  <li>Подбор осветительных приборов.</li>\r\n </ul>\r\n \r\n<ol type="1" start="2"> \r\n  <li>Поездки по магазинам и салонам. Консультации по выбору предметов интерьера. Каждый выезд оплачивается из расчета 4500 рублей за одну поездку. </li>\r\n </ol>\r\n \r\n<p> </p>\r\n \r\n<a name="6"></a>\r\n \r\n<h2>6. Авторский надзор.</h2>\r\n \r\n<p>1. Авторский надзор включает в себя: </p>\r\n \r\n<ul> \r\n  <li>регулярный выезд архитектора на объект (не реже 3-х раз в календарный месяц, но не более шести месяцев) </li>\r\n \r\n  <li>внесение в рабочие чертежи корректировок, возникших после демонтажа старых и возведения новых перегородок, после выравнивания полов, стен и потолков </li>\r\n \r\n  <li>консультации и контроль соблюдения выполнения данного проекта </li>\r\n \r\n  <li>заказ отделочных материалов </li>\r\n </ul>\r\n \r\n<p>2. Согласование счетов с Заказчиком на материалы и оборудование, их закупка и доставка на объект. </p>\r\n \r\n<p>3. Цена авторского надзора составляет:</p>\r\n \r\n<ul type="disc"> \r\n  <li>при площади объекта до 120 кв.м. - 900 рублей за&nbsp; кв.м. </li>\r\n \r\n  <li>при площади объекта от 120 кв.м. до 250 кв.м. – 800 рублей за кв.м. </li>\r\n \r\n  <li>при площади объекта от 250 кв.м. – 700 рублей за кв.м. </li>\r\n </ul>\r\n \r\n<p> </p>\r\n \r\n<a name="7"></a>\r\n \r\n<h2>7. Исполнительная документация.</h2>\r\n 1.&nbsp;&nbsp;&nbsp; Исполнительный проект по электрике – 90 рублей за 1 кв.м.&nbsp;&nbsp; Минимальная стоимость проекта&nbsp; 9000&nbsp; рублей. \r\n<br>\r\n 2.&nbsp;&nbsp;&nbsp; Исполнительный проект по сантехнике – 90 рублей за 1 кв.м.&nbsp; Минимальная стоимость проекта 9000 рублей. \r\n<br>\r\n 3.&nbsp;&nbsp;&nbsp; Исполнительный проект по отоплению – 60 рублей за 1 кв.м.&nbsp; Минимальная стоимость проекта 8000 рублей. \r\n<br>\r\n 4.&nbsp;&nbsp;&nbsp; Исполнительный проект кондиционирования (Сплит система) – 60 рублей за 1 кв.м. Минимальная стоимость 6000 рублей \r\n<br>\r\n 5.&nbsp;&nbsp;&nbsp; Исполнительный проект вентиляции и кондиционирования (канальный кондиционер) – 90 рублей за 1 кв.м. Минимальная стоимость 9000 рублей. \r\n<br>\r\n 6.&nbsp;&nbsp;&nbsp; Согласование проектов в службе эксплуатации дома – 12000 рублей. \r\n<br>\r\n 7.&nbsp;&nbsp;&nbsp; Сдача квартиры в эксплуатацию службе эксплуатации дома – 12000 рублей. \r\n<br>\r\n \r\n<br>\r\n \r\n<p></p>\r\n \r\n<p> \r\n  <br>\r\n </p>\r\n \r\n\r\n																			<div align="right">\r\n										<br>\r\n										<noindex>\r\n<table cellpadding="0" cellspacing="0" border="0">\r\n	<tbody><tr>\r\n		<td style="padding-right:10px;">\r\n			<!-- Разместите этот тег в том месте, где должна отображаться кнопка +1 -->\r\n			<div id="___plusone_0" style="text-indent: 0px; margin: 0px; padding: 0px; background-color: transparent; border-style: none; float: none; line-height: normal; font-size: 1px; vertical-align: baseline; display: inline-block; width: 90px; height: 20px; background-position: initial initial; background-repeat: initial initial;"><iframe frameborder="0" hspace="0" marginheight="0" marginwidth="0" scrolling="no" style="position: static; top: 0px; width: 90px; margin: 0px; border-style: none; left: 0px; visibility: visible; height: 20px;" tabindex="0" vspace="0" width="100%" id="I0_1385818591765" name="I0_1385818591765" src="https://apis.google.com/u/0/_/+1/fastbutton?usegapi=1&amp;bsv=o&amp;size=medium&amp;hl=ru&amp;origin=http%3A%2F%2Fwww.eremont.ru&amp;url=http%3A%2F%2Fwww.eremont.ru%2Fdesign%2Fprice.html&amp;gsrc=3p&amp;ic=1&amp;jsh=m%3B%2F_%2Fscs%2Fapps-static%2F_%2Fjs%2Fk%3Doz.gapi.ru.FmG4Zav8P34.O%2Fm%3D__features__%2Fam%3DEQ%2Frt%3Dj%2Fd%3D1%2Frs%3DAItRSTOtN0AXLa6eKRmZMqrtkqG35LH6uA#_methods=onPlusOne%2C_ready%2C_close%2C_open%2C_resizeMe%2C_renderstart%2Concircled%2Cdrefresh%2Cerefresh&amp;id=I0_1385818591765&amp;parent=http%3A%2F%2Fwww.eremont.r